mapper.select:
SELECT *
FROM records
WHERE userID = #{userID}
AND YEAR(recordDate) = YEAR(#{recordDate})
AND MONTH(recordDate) = MONTH(#{recordDate});

mapper.delete:
DELETE FROM records
WHERE userID = #{userID}
AND YEAR(recordDate) = YEAR(#{recordDate})
AND MONTH(recordDate) = MONTH(#{recordDate})
AND DAY(recordDate) = DAY(#{recordDate});
